import { useEffect, useRef, useState } from "react";
import "../App.css";
import "../reset.css";
import NoTodos from "./NoTodos";
import TodoForm from "./TodoForm";
import TodoList from "./TodoList";

function App() {
  const [name, setName] = useState("");
  const nameInputEl = useRef(null);
  //state we are defining and hook
  const [todos, setTodos] = useState([
    //todos is the state and setTodos is setter for state
    {
      id: 1,
      title: "finish React Series",
      isComplete: false,
      isEditing: false,
    },

    {
      id: 2,
      title: "Go Grocery",
      isComplete: true,
      isEditing: false,
    },

    {
      id: 3,
      title: "Take Over World",
      isComplete: false,
      isEditing: false,
    },
  ]);

  const [idForTodo, setIdForTodo] = useState(4);

  function addTodo(todo) {
    setTodos([
      ...todos,
      {
        //Making a copy of todo array and adding a new object
        id: idForTodo,
        title: todo,
        isComplete: false,
      },
    ]);

    //2 lines with input are for adding the todo

    setIdForTodo(prevIdForTodo => prevIdForTodo + 1);
  }

  //for deleting todo
  function deleteTodo(id) {
    // console.log("deleting todo id" + id);
    setTodos([...todos].filter(todo => todo.id !== id));
  }

  function completeTodo(id) {
    //to put line through the todos to mark as complete
    const updatedTodos = todos.map(todo => {
      if (todo.id === id) {
        todo.isComplete = !todo.isComplete;
      }
      return todo;
    });

    setTodos(updatedTodos);
  }

  //Function for double click to edit
  function markAsEditing(id) {
    //to put line through the todos to mark as complete
    const updatedTodos = todos.map(todo => {
      if (todo.id === id) {
        todo.isEditing = true;
      }
      return todo;
    });

    setTodos(updatedTodos);
  }

  //Function for Clicking off edit to close it
  function updateTodo(event, id) {
    // console.log(event.target.value);
    const updatedTodos = todos.map(todo => {
      if (todo.id === id) {
        if (event.target.value.trim().length === 0) {
          todo.isEditing = false;
          return todo;
        }
        todo.title = event.target.value;
        todo.isEditing = false;
      }
      return todo;
    });

    setTodos(updatedTodos);
  }

  //Function for pressing escape to cancel edit
  function cancelEdit(event, id) {
    //to put line through the todos to mark as complete
    const updatedTodos = todos.map(todo => {
      if (todo.id === id) {
        todo.isEditing = false;
      }
      return todo;
    });

    setTodos(updatedTodos);
  }
  //for the 3 items raimining
  function remaining() {
    return todos.filter(todo => !todo.isComplete).length;
  }

  function clearCompleted() {
    setTodos([...todos].filter(todo => !todo.isComplete));
  }

  function completeAllTodos() {
    const updatedTodos = todos.map(todo => {
      todo.isComplete = true;

      return todo;
    });

    setTodos(updatedTodos);
  }

  function todosFiltered(filter) {
    if (filter === "all") {
      return todos;
    } else if (filter === "active") {
      return todos.filter(todo => !todo.isComplete);
    } else if (filter === "completed") {
      return todos.filter(todo => todo.isComplete);
    }
  }

  useEffect(() => {
    console.log("use EFFECt");
  });
  return (
    <div className="todo-app-container">
      <div className="todo-app">
        <div className="name-Container">
          <h2>What is Your Name?</h2>
          <button onClick={() => nameInputEl.current.focus()}>get Ref</button>
          <form action="£">
            <input
              type="text"
              ref={nameInputEl}
              className="todo-input"
              placeholder="What is your name?"
              value={name}
              onChange={event => setName(event.target.value)}
            />
          </form>
          {name && <p className="name-label">Hello, {name}</p>}
        </div>
        <h2>Todo App</h2>
        <TodoForm addTodo={addTodo} />
        {/* looping over  */}
        {todos.length > 0 ? (
          <TodoList
            todos={todos}
            completeTodo={completeTodo}
            markAsEditing={markAsEditing}
            updateTodo={updateTodo}
            cancelEdit={cancelEdit}
            deleteTodo={deleteTodo}
            remaining={remaining}
            clearCompleted={clearCompleted}
            completeAllTodos={completeAllTodos}
            todosFiltered={todosFiltered}
          />
        ) : (
          <NoTodos />
        )}
      </div>
    </div>
  );
}

export default App;
