import { useState } from "react";
import "../App.css";
import "../reset.css";

function App() {
  //state we are defining and hook
  const [todos, setTodos] = useState([
    //todos is the state and setTodos is setter for state
    {
      id: 1,
      title: "finish React Series",
      isComplete: false,
    },
    {
      id: 2,
      title: "Go Grocery",
      isComplete: true,
    },
    {
      id: 3,
      title: "Take Over World",
      isComplete: false,
    },
  ]);

  const [todoInput, setTodoInput] = useState("");
  const [idForTodo, setIdForTodo] = useState(4);

  function addTodo(event) {
    event.preventDefault();

    //to check if string is empty
    if (todoInput.trim().length === 0) {
      return;
    }
    setTodos([
      ...todos,
      {
        //Making a copy of todo array and adding a new object
        id: idForTodo,
        title: todoInput,
        isComplete: false,
      },
    ]);
    //2 lines with input are for adding the todo
    setTodoInput("");
    setIdForTodo(prevIdForTodo => prevIdForTodo + 1);
  }

  //for deleting todo
  function deleteTodo(id) {
    // console.log("deleting todo id" + id);
    setTodos([...todos].filter(todo => todo.id !== id));
  }

  function handleInput(event) {
    setTodoInput(event.target.value); //this is what the user typed in
  }

  function completeTodo(id) {
    //to put line through the todos to mark as complete
    const updatedTodos = todos.map(todo => {
      if (todo.id === id) {
        todo.isComplete = !todo.isComplete;
      }
      return todo;
    });

    setTodos(updatedTodos);
  }
  return (
    <div className="todo-app-container">
      <div className="todo-app">
        <h2>Todo App</h2>
        <form action="#" onSubmit={addTodo}>
          <input
            type="text"
            value={todoInput}
            onChange={handleInput}
            className="todo-input"
            placeholder="What do you need to do?"
          />
        </form>
        {/* looping over  */}

        <ul className="todo-list">
          {todos.map((todo, index) => (
            <li key={todo.id} className="todo-item-container">
              <div className="todo-item">
                <input type="checkbox" onChange={() => completeTodo(todo.id)} />
                <span
                  className={`todo-item-label ${
                    todo.isComplete ? "line-through" : ""
                  }`}
                >
                  {todo.title}
                </span>
                {/* <input type="text" className="todo-item-input" value="Finish React Series" /> */}
              </div>
              <button onClick={() => deleteTodo(todo.id)} className="x-button">
                <svg
                  className="x-button-icon"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M6 18L18 6M6 6l12 12"
                  />
                </svg>
              </button>
            </li>
          ))}
        </ul>

        <div className="check-all-container">
          <div>
            <div className="button">Check All</div>
          </div>

          <span>3 items remaining</span>
        </div>

        <div className="other-buttons-container">
          <div>
            <button className="button filter-button filter-button-active">
              All
            </button>
            <button className="button filter-button">Active</button>
            <button className="button filter-button">Completed</button>
          </div>
          <div>
            <button className="button">Clear completed</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
